import React from "react"
import style from "./NewMessage.module.css"
import {Field, reduxForm} from "redux-form";

const NewMessageForm = (props) => {

    // let newText = React.createRef()
    //
    // let updateMessage = () => {
    //     let text = newText.current.value
    //     props.onUpdateTextMessage(text)
    // }
    //
    // let addMessage = () => {
    //     let userId = props.dialogsPage.dialogs.id
    //     props.onAddMessage(userId)
    // }

    return (
        <form onSubmit={props.handleSubmit} className={style.newMessage}>
            <div>
                <Field component={"textarea"} placeholder="Write you message" name={"newMessageText"} />
            </div>
            <div>
                <button onClick={addMessage}>Send</button>
            </div>
        </form>
    )
}

const NewMessageReduxForm = reduxForm ({form:"newMessageForm"}) (NewMessageForm)


const NewMessage = (props) => {
    let addMessage = (value) =>{
        console.log(value)
    }
    return(
        <div>
            <NewMessageReduxForm onSubmit={addMessage}/>
        </div>
    )
}

export default NewMessage
