import React from "react"
import style from "./MyPosts.module.css"
import Post from "./Post/Post";
import NewPost from "./NewPost/NewPost";
import Preloader from "../../common/Preloader/Preloader";

const MyPosts = React.memo(props => {

    if (props.profilePage.profile == null) {
        return <Preloader/>
    }

    let postsElement =
        props.profilePage.posts.map(p => <Post
            lickCount={p.lickCount} text={p.text} key={p.id} avatar={props.profilePage.profile.photos.small}/>)

    let contacts = () => {
        for (let key in props.profile.contacts) {
            const contactsArray = []
            contactsArray.push(<div>key:props.profile.contacts[key] </div>)
        }
    }

    return (
        <div className={style.posts}>

            <div className={style.profileInfo}> ProfileInfoBlock
                <div>{props.profile.fullName}</div>
                <div>{props.status}</div>
                ----------------------------------------------------------------------
                <div className={style.item}> About me: </div>
                <div> {props.profile.aboutMe ? props.profile.aboutMe : "It's empty here so far"} </div>
                {props.profile.lookingForAJob ?
                    <div>props.profile.lookingForAJobDescription</div> :
                    "there may be looking for job"}
                <div>Contacts:</div>
                {contactsArray}

            </div>

            <div className={style.info}>
                <h2>Posts</h2>
                <NewPost onAddPost={props.onAddPost} state={props.profilePage}/>
            </div>
            <div className={style.post}>
                {postsElement}
            </div>

        </div>
    )
});

export default MyPosts
