import React from "react"
import {addPostAC, updateNewTextAC} from "../../../../Redux/profileReducer";
import StoreContext from "../../../../storeContext";
import NewPost from "./NewPost";

const NewPostConteiner = (props) => {

    return (

        <StoreContext.Consumer>
            {(store) => {
                let state = store.getState();
                let onTextChange = (text) => {
                    store.dispatch(updateNewTextAC(text));
                }

                let onAddPost = () => {
                    store.dispatch(addPostAC());
                }
                return (<NewPost onTextChange={onTextChange}
                                 onAddPost={onAddPost}
                                 newText={state.profilePage.newText}/>
                )
            }}
        </StoreContext.Consumer>
    )
}

export default NewPostConteiner
