import React from "react"
import {connect} from "react-redux";
import {getDialogs, getMessages, sendNewMessage, startDialog} from "../../Redux/dialogsReducer";
import Dialogs from "./Dialogs";
import withAuthRedirect from "../../HOC/withAuthRedirect";

class DialogsContainer extends React.Component {

    componentDidMount() {
        this.props.getDialogs()
    }
componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.messages !== this.props.messages){
            this.props.getMessages(this.props.activeDialogId)
        }
        if(prevProps.dialogs !== this.props.dialogs){
            this.props.getDialogs()
        }
}

    render() {
        return <Dialogs sendNewMessage={this.props.sendNewMessage} getDialogs={this.props.getDialogs}
                        startDialog={this.props.startDialog} getMessages={this.props.getMessages}
                        dialogsPage={this.props.dialogsPage} isAuth={this.props.isAuth}
                        userId={this.props.userId} dialogs={this.props.dialogs}
                        getMessages={this.props.getMessages} messages={this.props.messages}
                        sendNewMessage={this.props.sendNewMessage}
                        activeDialogId={this.props.activeDialogId}/>
    }


}

let mapStateToProps = (state) => {
    return {
        dialogsPage: state.dialogsPage,
        isAuth: state.auth.isAuth,
        userId: state.dialogsPage.userId,
        dialogs: state.dialogsPage.dialogs,
        messages: state.dialogsPage.messages,
        activeDialogId: state.dialogsPage.activeDialogId,
    }
}

let AuthRedirectComponent = withAuthRedirect(DialogsContainer)

export default connect(mapStateToProps, {sendNewMessage, getDialogs, startDialog, getMessages})(AuthRedirectComponent)
