import React from "react";
import style from "./Users.module.css"
import User from "./User/User";

const Users = (props) => {
    let pagesCount = Math.ceil(props.totalCount / props.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }

    let switchingPage = () => {
       //p[0]; first page of array
       //  prevOfCurrentPage, currentPage, nextOfCurrentPage
      //p[pagesCount]; last page of array
          console.log ( `${pages[0]} ...${props.currentPage-1} ${props.currentPage} ${props.currentPage+1} ... ${pagesCount}`)

    }

    return (

        <div className={style.users}>
            {switchingPage()}
            <div>
                <button type={"button"} className={style.button}> {"<<"} </button>
                {pages.map(p => {
                    return <span className={props.currentPage === p && style.selected}
                                 onClick={(e) => {
                                     props.onPageChanged(p)
                                 }}>{p} </span>
                })}
                <button type={"button"} className={style.button}>{">>"}</button>
            </div>
            <div>{props.users.map(u => {
                return <User id={u.id} key={u.id} avatar={u.photos.small} name={u.name}
                             country={"Bikini bottom"}
                             city={"Seabed"}
                             followed={u.followed}
                             follow={props.follow}
                             unfollow={props.unfollow}
                             followingInProgress={props.followingInProgress}
                             toggleFollowingProgress={props.toggleFollowingProgress}/>
            })}</div>
        </div>
    )
}


export default Users
