import React from "react"
import style from "./NewPost.module.css"

const NewPost = (props) => {

    let newText = React.createRef()

    const textChange = () => {
        let text = newText.current.value
        props.state.profilePage.newText = text
        console.log (        props.state.profilePage.newText)
    }

    const addPost = () => {
        let text = newText.current.value
        let newPost = {
            id: 4,
            text: text, lickCount:0,
            avatar: "https://upload.wikimedia.org/wikipedia/en/thumb/3/3b/SpongeBob_SquarePants_character.svg/1200px-SpongeBob_SquarePants_character.svg.png"
        }
        props.state.profilePage.posts.push(newPost)
        newText.current.value = ""
    }

    return (
        <div className={style.newMessage}>
            <div>
                <textarea onChange={textChange} ref={newText} placeholder="Write you post text"/>
            </div>
            <div>
                <button onClick={addPost}>Post</button>
            </div>
        </div>
    )
}

export default NewPost
