import React from "react"
import style from "./Message.module.css"
import {NavLink} from "react-router-dom";

// id={m.id} key={m.id} messageText={m.body}
// recipientId={m.recipientId} senderId={m.senderId}
// senderName={m.senderName}
// translatedBody={m.translatedBody}
// viewed={m.viewed} totalCount={m.totalCount}
// addedAt={m.addedAt}

class Message extends React.Component {

    componentDidUpdate(prevProps, prevState, snapshot) {
        debugger
        if (this.props.messages.items !== undefined && prevProps.messages.items !== undefined) {
            if (prevProps.messages.items.length !== this.props.messages.items.length) {
                debugger
                this.props.getMessages(this.props.activeDialogId)
            }
        }
    }

    render() {


        return (
            <div className={this.props.viewed ? style.message : (style.message + " " + style.viewed)}>
                <div>
                    <NavLink to={`/profile/${this.props.senderId}`}>
                        <span className={style.name}>{this.props.senderName}</span>
                    </NavLink>
                    <span className={style.date}>{this.props.addedAt}</span>

                </div>
                <div className={style.text}>{this.props.messageText}</div>
            </div>
        )
    }
}

export default Message
