class Message extends React.Component {

    componentDidUpdate(prevProps, prevState, snapshot) {
        debugger
        if (this.props.messages.items !== undefined && prevProps.messages.items !== undefined) {
            if (prevProps.messages.items.length !== this.props.messages.items.length) {
                debugger
                this.props.getMessages(this.props.activeDialogId)
            }
        }
    }

    render() {


        return (
            <Message id={this.props.id} messageText={this.props.body}
                     recipientId={this.props.recipientId} senderId={this.props.senderId}
                     senderName={this.props.senderName}
                     translatedBody={this.props.translatedBody}
                     viewed={this.props.viewed} totalCount={this.props.totalCount}
                     addedAt={this.props.addedAt}/>
        )
    }
}

export default Message
