import {NavLink} from "react-router-dom";
import avatar from "../../assets/images/avatar.png"
import Preloader from "../common/Preloader/Preloader";

const HeaderInfo = (props) => {

    const [display, setDisplay] = useState(false)

    // let openDropdownMenu = () => {
    //     // alert("drop")
    // }
    // let closeDropdownMenu = () => {
    //     alert("undrop")
    // }

    return (

        <div className={style.info}>
            {/*{props.isAuth? <div><div>{props.login}</div> <button onClick={props.Logout} className={style.logout}>Logout</button></div>*/}
            {/*    :<NavLink to="/login/" className={style.loginButton}> Login</NavLink>}*/}

            {props.profile ?
                <img onClick={setDisplay(true)} onBlur={setDisplay(false)} className={style.avatar}
                     src={props.profile.photos.small ? props.profile.photos.small : avatar}/> :
                <Preloader/>}
            {/*{display?<div className={style.dropdownMenu}>*/}
            {/*    <button onClick={props.Logout} className={style.logout}>Logout</button>*/}
            {/*    <button onClick={() => {*/}
            {/*        alert("Open settings")*/}
            {/*    }} className={style.settings}>Settings*/}
            {/*    </button>*/}
            {/*</div>: null}*/}
        </div>
    )
}

export default HeaderInfo
