import React, {useState} from "react"
import style from "./ProfileInfo.module.css"
import Preloader from "../../common/Preloader/Preloader";
import Status from "./ProfileStatus/Status";
import avatar from "../../../assets/images/avatar1.jpg"
import StatusWithHooks from "./ProfileStatus/StatusWithHooks";
import loadFile from "../../../assets/loadFile2.jpg"
import {setStatus} from "../../../Redux/profileReducer";

const ProfileInfo = (props) => {
    if (!props.profile) {
        return <Preloader/>
    }

    const onMainPhotoSelected = (e) => {
        if (e.target.files.length) {
            props.savePhoto(e.target.files[0])
        }
    }

    const [loadPhoto, setLoadPhoto] = useState(false)

    let adc = loadPhoto;
    // let loadPhoto = false;
    //
    // let mouseE = (e) => {
    //     loadPhoto = e
    //     }

    return (
        <div className={style.info}>
            <div className={style.avatar}>
                {(props.isOwner) ?
                    <div><input className={style.avatarLoader} type={"file"} id={"avatarLoader"}
                                onChange={onMainPhotoSelected}/>
                        <label for={"avatarLoader"}> <img className={style.avatarImg}
                                                          onMouseOver={()=>{setLoadPhoto(true)}}
                                                          onMouseOut={()=>{setLoadPhoto(false)}}

                                                          src={props.profile.photos.large !== null ?
                                                              props.profile.photos.large : avatar}/>
                            {adc?<img className={style.avatarLoaderIcon} src={loadFile}/>:null}
                        </label>
                    </div> : <img src={props.profile.photos.large !== null ?
                        props.profile.photos.large : avatar}/>}
            </div>
        </div>
    )
}

export default ProfileInfo
