import React from "react"
import {connect} from "react-redux";
import {sendMessageAC, updateMessageTextAC} from "../../Redux/dialogsReducer";
import Dialogs from "./Dialogs";
import withAuthRedirect from "../../HOC/wihtAuthRedirect";


let mapStateToProps = (state) => {
    return {
        dialogsPage: state.dialogsPage,
        isAuth: state.auth.isAuth
    }
}

let AuthRedirectComponent = withAuthRedirect(Dialogs)

let mapDispatchToProps = (dispatch) => {
    return {
        onAddMessage: (userId,newText) => {
            dispatch(sendMessageAC(userId,newText))
        },
        onUpdateTextMessage: (text) => {
            dispatch(updateMessageTextAC(text))
        },
    }
}

let DialogsContainer = connect(mapStateToProps, mapDispatchToProps)(AuthRedirectComponent)

export default DialogsContainer
