import React from "react";
import style from "./User.module.css"

const User = (props) => {
    let unfollow = (userId) => {
        console.log(props.followed)
        props.unfollow(userId)
    }
    let follow = (userId) => {
        console.log(props.followed)
        props.follow(userId)
    }

    return (
        <div className={style.user}>
            <img src={props.avatar}/>
            <div> {props.name}</div>
            <div> {props.country}</div>
            <div> {props.city}</div>
            {/*<div><button onClick={unfollow}>unfollow</button></div>*/}
            {/*<div><button onClick={follow}>Follow</button></div>*/}
            <div>{ props.followed?<button onClick={unfollow}> Unfollow</button>:<button onClick={follow}> Follow</button>}</div>
        </div>
    )
}

export default User
