import React from "react";
import style from "./User.module.css"
import avatar from "../../../assets/images/avatar1.jpg"
import {NavLink} from "react-router-dom";
import * as axios from "axios";

const User = (props) => {
    let unfollow = () => {
        // props.unfollow(props.id)
        axios.delet(`https://social-network.samuraijs.com/api/1.0/follow${props.id}`).then(response => {
            if (response.data.resultCode === 0) {
              props.unfollow(props.id)
            }

        });
    }
    let follow = () => {
        // props.follow(props.id)
        // console.log(props.followed)
        axios.post(`https://social-network.samuraijs.com/api/1.0/users?follow${props.id}`).then(response => {
            if (response.data.resultCode === 0) {
                props.follow(props.id)
            }
        });

    }
    return (
        <div>
            <NavLink to={"/profile/" + props.id} className={style.user}>
                <img src={props.avatar !== null ? props.avatar : avatar}/>
            </NavLink>

            <div> {props.name}</div>
            <div> {props.country}</div>
            <div> {props.city}</div>
            <div>{props.followed ?
                <button onClick={unfollow}> Unfollow</button> :
                <button onClick={follow}> Follow</button>}</div>
        </div>)
}

export default User
