    //     props.unfollow(userId)
    // }
    let follow = (userId) => {
        alert("!!!!")
        // console.log(props.followed)
        // props.follow(userId)
    }

    return (
        <div className={style.user}>
            <img src={props.avatar}/>
            <div> {props.name}</div>
            <div> {props.country}</div>
            <div> {props.city}</div>
            {/*<div><button onClick={unfollow}>unfollow</button></div>*/}
            <div><button onclick={follow}>Follow</button></div>
            {/*<div>{ props.followed?<button onClick={props.follow}> Follow</button>:<button onClick={unfollow(props.key)}> Unfollow</button>}</div>*/}
        </div>
    )
}

export default User
