import React from "react"
import Message from "./Message";
import spamIcon from "../../../../assets/spamIcon.png"
import deleteIcon from "../../../../assets/deleteIcon.png"

const Messages = (props) => {

    let addToSpam = () => {
        props.sendMessageInSpam(props.id)
    }

    let deleteMessage = () => {
        props.deleteMessageForMe(props.id)
    }

    let restoreMessage = () => {
        props.restoreMessage(props.id)
    }

    if (props.senderId !== props.authUserId) {
        props.setMessageViewed(props.id);
        props.spamMessagesId.map(s => {
            if (s == props.id) {
                return (
                    <Message id={props.id} messageText={props.body}
                             recipientId={props.recipientId}
                             senderId={props.senderId}
                             senderName={props.senderName}
                             translatedBody={props.translatedBody}
                             viewed={props.viewed}
                             totalCount={props.totalCount}
                             addedAt={props.addedAt}
                             authUserId={props.authUserId}
                             setMessageViewed={props.setMessageViewed}
                             sendMessageInSpam={addToSpam}
                             deleteMessageForMe={deleteMessage}
                             buttonClick={restoreMessage}
                             buttonIcon={spamIcon}/>
                )
            }
        })
    } else {
        return (
            <Message id={props.id} messageText={props.body}
                     recipientId={props.recipientId}
                     senderId={props.senderId}
                     senderName={props.senderName}
                     translatedBody={props.translatedBody}
                     viewed={props.viewed}
                     totalCount={props.totalCount}
                     addedAt={props.addedAt}
                     authUserId={props.authUserId}
                     setMessageViewed={props.setMessageViewed}
                     sendMessageInSpam={addToSpam}
                     deleteMessageForMe={deleteMessage}
                     buttonClick={addToSpam}
                     buttonIcon={spamIcon}/>
        )
    }

    if (props.senderId == props.authUserId) {
        props.deleteMessagesId.map(s => {
            if (s == props.id) {
                return (
                    <Message id={props.id} messageText={props.body}
                             recipientId={props.recipientId}
                             senderId={props.senderId}
                             senderName={props.senderName}
                             translatedBody={props.translatedBody}
                             viewed={props.viewed}
                             totalCount={props.totalCount}
                             addedAt={props.addedAt}
                             authUserId={props.authUserId}
                             setMessageViewed={props.setMessageViewed}
                             sendMessageInSpam={addToSpam}
                             deleteMessageForMe={deleteMessage}
                             buttonClick={restoreMessage}
                             buttonIcon={deleteIcon}/>
                )
            }
        })
    } else {
        return (
            <Message id={props.id} messageText={props.body}
                     recipientId={props.recipientId}
                     senderId={props.senderId}
                     senderName={props.senderName}
                     translatedBody={props.translatedBody}
                     viewed={props.viewed}
                     totalCount={props.totalCount}
                     addedAt={props.addedAt}
                     authUserId={props.authUserId}
                     setMessageViewed={props.setMessageViewed}
                     sendMessageInSpam={addToSpam}
                     deleteMessageForMe={deleteMessage}
                     buttonClick={deleteMessage} buttonIcon={deleteIcon}/>
        )
    }


}

export default Messages
