import React from "react"
import style from "./FormControl.module.css"

// const textError = (meta, input) =>{
//     if (meta.error && input.value!=0) return style.errorText
//     return undefined
// }

// textError(meta, input)
const textError = (meta, input) => {
    if (meta.error && meta.touched && !meta.active) return <span className={style.errorText}>{meta.error}</span>

}

export const Textarea = ({input, meta, ...props}) => {
    return (
        <div className={style.formControl + " " + style.error}>
            <textarea {...input} {...props}/>

            {textError(meta, input)}
        </div>
    )
}
