import React from "react"
import style from "./NewPost.module.css"
import {Field, reduxForm} from "redux-form";

const NewPostForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit} className={style.newMessage}>
            <div>
                <Field component={"textarea"} placeholder={"Enter new post"} name={"newPostText"} />
            </div>
            <div>
                <button>Post</button>
            </div>
        </form>
    )
}

const NewPostReduxForm = reduxForm ({form:"newPostText"})(NewPostForm);

const NewPost = (props) => {
    let addPost = (value)=>{
        console.log(value)
    }
    return(
        <div>
            <NewPostReduxForm onSubmit={addPost} />
        </div>
    )
}




