import React from "react"

const SET_USER_DATA = "SET_USER_DATA";


let initialization = {
    userId: null,
    email: null,
    login: null,
    isFetching: false,
    isAuth:false,
}

const authReducer = (state = initialization, action) => {
    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                ...action.data,
                isAuth:true,
            }

        default:
            return state
    }
}

export const setAuthUserData = (id, email, login) => ({
    type: SET_USER_DATA, data: {
        id, email, login
    }
})

export default authReducer;
