import React from "react"
import style from "./Dialog.module.css"
import Message from "./Message/Message";
import User from "./User/User";
import store from "../../../Redux/state";

// {
//     id: 1, name: "Sandy",
//     avatar: "https://www.standingstills.com/pub/media/catalog/product/cache/75eed2686e01eb22cb4050b2f40ddf97/c/a/cac165-1-1.jpg",
//     messages: [
//     {id: 1, text: "Good!",},
//     {id: 2, text: "Ger!",},
//     {id: 3, text: "Gl!",},
// ]
// }

const Dialog = (props) => {

    let usersElement = store.getState().dialogsPage.dialogs.map( u =><User id={u.id} avatar={u.avatar} name={u.name} key={u.id}/>)

    let messageElement = store.getState().dialogsPage.dialogs.map( m => <Message key={m.id} messages={m.messages}/>)

    return (
        <div className={style.dialog}>
            <div className={style.users}>
                {usersElement}

            </div>

            <div className={style.messages}>
                <Message/>
                <Message/>
                <Message/>

            </div>
        </div>
    )
}

export default Dialog
