import React from "react"
import style from "./Dialogs.module.css"
import NewMessage from "./NewMessage/NewMessage";
import User from "./Dialog/User/User";
import Message from "./Dialog/Message/Message";
import {sendNewMessage} from "../../Redux/dialogsReducer";


const Dialogs = (props) => {
// debugger
//
//     hasNewMessages: false
//     id: 2
//     lastDialogActivityDate: "2020-04-24T08:29:29.453"
//     lastUserActivityDate: "2020-04-23T12:24:37.177"
//     newMessagesCount: 0
//     photos:
//         large: "https://social-network.samuraijs.com/activecontent/images/users/2/user.jpg?v=1"
//     small: "https://social-network.samuraijs.com/activecontent/images/users/2/user-small.jpg?v=1"
//     __proto__: Object
//     userName: "samurai"

    let usersElement = props.dialogs.map(u => <User id={u.id}
                                                                avatar={u.photos.small}
                                                                name={u.userName}
                                                                key={u.id}
                                                                dialogs={props.dialogs}
                                                    newMessagesCount={props.newMessagesCount}
                                                    lastUserActivityDate={props.lastUserActivityDate}
                                                    lastDialogActivityDate={props.lastDialogActivityDate}
    />)

    // let messageElement = props.dialogsPage.messages.map(m => <Message id={m.id}
    //                                                                   key={m.id}
    //                                                                   messages={m.text}/>)

    return (
        <div className={style.dialogs}>

            <div className={style.users}>
                {usersElement}

            </div>

            <div className={style.messages}>
                {/*{messageElement}*/}
            </div>

            <div className={style.newMessage}>
                <NewMessage dialogsPage={props.dialogsPage}
                            sendNewMessage={props.sendNewMessage}
                            userId={props.userId}/>
            </div>
        </div>
    )
}

export default Dialogs
