import React, {useState} from "react";
import style from "./Pagination.module.css";

const Pagination = ({totalCount, pageSize, paginationSize, ...props}) => {
    let pagesCount = Math.ceil(totalCount / pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }

    let [currentPage, setCurrentPage] = useState(1)

    let leftBorder = currentPage - Math.ceil((paginationSize - 1) / 2);

    let rightBorder = currentPage + Math.ceil((paginationSize - 1) / 2);

    let pagination = pages.filter(p => p >= leftBorder).filter(p => p <= rightBorder)

    console.log(pagination)

    return (
        <div>
            {currentPage <= 5 ? <button className={style.button}>{""}</button> :
                <button type={"button"} onClick={() => {
                    setCurrentPage(currentPage - 1)
                }} className={style.button}> {"<"} </button>
            }

            {pagination.map(p => {
                return <span className={style.pageNumber +" "+ (currentPage === p && style.selected)}
                             onClick={(e) => {
                                 setCurrentPage(p)
                             }}>{p} </span>
            })}
            <button type={"button"} onClick={() => {
                setCurrentPage(currentPage + 1)
            }} className={style.button}>{">"}</button>
        </div>
    )
}

export default Pagination
