import React from "react";
import style from "./Users.module.css"
import User from "./User/User";
import * as axios from "axios";

class Users extends React.Component {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.currentPage}`).then(response => {
            this.props.setUsers(response.data.items)
        });

    }

    onPageChanged = (pageNumber) => {
        this.props.setCurrentPage(pageNumber)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.currentPage}`).then(response => {
            this.props.setUsers(response.data.items)
        });
    }

    render() {

        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
        let pages = [];
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i)
        }

        return (
            <div className={style.users}>
                <div>
                    {pages.map(p => {
                        return <span className={this.props.currentPage === p && style.selected}
                                     onClick={(e) => {
                                         this.onPageChanged(p)
                                     }}>{p}</span>
                    })}
                </div>
                <div>{this.props.users.map(u => (<User id={u.id} key={u.id} avatar={u.photos.small} name={u.name}
                                                       country={"Bikini bottom"}
                                                       city={"Seabed"}
                                                       followed={u.followed}
                                                       follow={this.props.follow}
                                                       unfollow={this.props.unfollow}/>))}</div>
            </div>
        )
    }
}

export default Users
