//             {/*<input {...input} {...props}/>*/}
//             {props.children}
//             {hasError? <span className={style.errorText}>{meta.error}</span> : undefined}
//         </div>
//     )
// }
// export const Textarea1 = (props) => {
//     const {input,meta, child, ...restProps} = props
//     return <FormControl {...props}> <textarea {...input} {...restProps}/> </FormControl>
// }


const textError = (meta) => {
    const hasError = meta.error && meta.touched && !meta.active;

    if (hasError) return <span className={style.errorText}>{meta.error}</span>
    return undefined
}

const errorBorder = (meta) => {
    const hasError = meta.error && meta.touched && !meta.active;

    if (hasError) return style.formControl + " " + style.error
    return undefined
}

export const Textarea = ({input, meta, ...props}) => {

    return (
        <div className={errorBorder(meta)}>
            <textarea {...input} {...props}/>

            {textError(meta)}
        </div>
    )
}

export const Input = ({input, meta, ...props}) => {
    return (
        <div className={errorBorder(meta)}>
            <input {...input} {...props}/>

            {textError(meta)}
        </div>
    )
}
