import React, {useState} from "react";
import style from "./Pagination.module.css";

const Pagination = ({totalCount, pageSize, currentPage, paginationSize, ...props}) => {
    let pagesCount = Math.ceil(totalCount / pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }

    let [page, setPage] = useState(currentPage)

    let leftBorder = page - Math.ceil((paginationSize - 1) / 2);

    let rightBorder = page + Math.ceil((paginationSize - 1) / 2);

    let endPage = pages[pages.length-1]

    let pagination = pages.filter(p => p >= leftBorder).filter(p => p <= rightBorder)

    pagination.push(endPage)

    console.log(endPage)
    // console.log(pagination.push(endPage))
    console.log(pagesCount)
    console.log(pagination)

    return (
        <div className={style.pagination}>
            <div className={style.button} >{page <= 5 ? <button ></button> :
                <button type={"button"} onClick={() => {
                    setPage(page - 1)
                }} className={style.pageButton}> {"<"} </button>
            }</div>

            {pagination.map(p => {
                return <div className={style.pageNumber +" "+ (currentPage === p && style.selected)}
                             onClick={(e) => {
                                 props.onPageChanged(p)
                             }}>{p} </div>
            })}

            <div className={style.button}>{page >= (pagesCount) ? <button>{""}</button> :
                <button type={"button"} onClick={() => {
                    setPage(page+1)
                }} className={style.pageButton}> {">"} </button>
            }</div>

        </div>
    )
}

export default Pagination
