import React, {useState} from "react"
import Message from "./Message";
import {reversedCopy} from "../../../../utils/arrayHalper";
import {Route, Switch} from "react-router-dom";
import withSuspense from "../../../../HOC/withSuspens";

// recipientId={m.recipientId} senderId={m.senderId}
// senderName={m.senderName}
// translatedBody={m.translatedBody}
//  totalCount={m.totalCount}

const Messages = (props) => {

    let [numberOfPage, setNumberOfPage] = useState(1)

    let reversMessageElement = null;

    let loadNextMessagesPage = () => {
        let pageCount = Math.ceil(props.dialogsPage.messages.totalCount / props.pageSize)
        if (numberOfPage >= pageCount) {
            return <div> It's all </div>
        }
        props.getMessages(props.activeDialogId, props.messagePageSize, (numberOfPage+1))
        let startItem = numberOfPage * props.pageSize;
        let endItem = startItem + props.pageSize;
        let element = props.dialogsPage.messagesItems.slice(startItem, endItem);

        setNumberOfPage(numberOfPage + 1);

        let reversElement = reversedCopy(element).map(m => <Message id={m.id} key={m.id} messageText={m.body}
                                                                    recipientId={m.recipientId}
                                                                    senderId={m.senderId}
                                                                    senderName={m.senderName}
                                                                    translatedBody={m.translatedBody}
                                                                    viewed={m.viewed}
                                                                    totalCount={m.totalCount}
                                                                    addedAt={m.addedAt}/>)
        return <React.Suspense fallback={<div>Loading...</div>}>{reversElement}</React.Suspense>
        // const DialogsContainer = React.lazy(() => import("./components/Dialogs/DialogsContainer"))
        //     <Route path="/dialogs" render={() => withSuspense(DialogsContainer)}/>


    }
    if (props.activeDialogId) {
        let messageElement = props.dialogsPage.messagesItems;
        reversMessageElement = reversedCopy(messageElement).map(m => <Message id={m.id} key={m.id} messageText={m.body}
                                                                              recipientId={m.recipientId}
                                                                              senderId={m.senderId}
                                                                              senderName={m.senderName}
                                                                              translatedBody={m.translatedBody}
                                                                              viewed={m.viewed}
                                                                              totalCount={m.totalCount}
                                                                              addedAt={m.addedAt}/>)
    }

    return (
        <div>
            {reversMessageElement}
           <button onClick={loadNextMessagesPage}>Load more messages</button>
        </div>
    )
}

export default Messages
