import React from "react"
import {connect} from "react-redux";
import {getDialogs, sendMessageAC} from "../../Redux/dialogsReducer";
import Dialogs from "./Dialogs";
import withAuthRedirect from "../../HOC/withAuthRedirect";


let mapStateToProps = (state) => {
    return {
        dialogsPage: state.dialogsPage,
        isAuth: state.auth.isAuth
    }
}

let AuthRedirectComponent = withAuthRedirect(Dialogs)

let mapDispatchToProps = (dispatch) => {
    return {
        onAddMessage: (newText, userId) => {
            dispatch(sendMessageAC(newText, userId))
        },
    }
}

let DialogsContainer = connect(mapStateToProps, {mapDispatchToProps, getDialogs})(AuthRedirectComponent)

export default DialogsContainer
