import React from "react";
import Profile from "./Profile";
import {setUsersProfile} from "../../Redux/profileReducer";
import {connect} from "react-redux";
import {Redirect, withRouter} from "react-router-dom";
import withAuthRedirect from "../../HOC/wihtAuthRedirect";

class ProfileContainer extends React.Component {

    componentDidMount() {
        let userId = this.props.match.params.userId
        this.props.setUsersProfile(userId)
    }

    render() {


        return (
            <Profile {...this.props} profile={this.props.profile}/>
        )
    }
}

let AuthRedirectComponent = withAuthRedirect(ProfileContainer)

let mapStateToPropsForRedirect = (state) =>({
    isAuth: state.auth.isAuth
})

AuthRedirectComponent = connect (mapStateToPropsForRedirect)(AuthRedirectComponent)

let mapStateToProps = (state) =>  ({
    profile: state.profilePage.profile,
})

let WithRouterProfileContainer = withRouter(AuthRedirectComponent)

export default connect(mapStateToProps, {setUsersProfile})(WithRouterProfileContainer)
