import * as axios from "axios"

const instance = axios.create({
    withCredentials: true,
    baseURL: "https://social-network.samuraijs.com/api/1.0/",
    headers: {"API-KEY": "7dfb43ec-1f78-472e-acf3-1edf7e984eb7"},
});

export const usersAPI = {
    getUsers: (currentPage = 1, pageSize = 10) => {
        return instance.get(`users?page=${currentPage}&count=${pageSize}`,)
            .then(response => {
                return response.data
            })
    },
    unfollowUser: (id) => {
        return instance.delete(`follow/${id}`)
            .then(response => {
                return response.data
            })
    },
    followUser: (id) => {
        return instance.post(`follow/${id}`)
            .then(response => {
                return response.data
            })
    },
    logIn: () => {
        console.warn("Obsolete method. Pleas authAPI object method")
        return authAPI.me()
        // return instance.get(`auth/me`)
        //     .then(response => {
        //         return response.data
        //     })
    },
    setUsers: (id) => {
        console.warn("Obsolete method. Pleas profileAPI object method")
        return profileAPI.setUsers(id)
    }
}

export const authAPI = {
    me:() => {
        return instance.get(`auth/me`)
    }
}

export const profileAPI = {
    setUsers: (id) => {
        return instance.get(`profile/` + id)
    },
    getStatus: (id) => {
        return instance.get(`status/`+ id)
    },

    updateStatus: (status)=>{
        return instance.put(`status/`, {status:status})
    }
}
