import React from "react"
import style from "./NewPost.module.css"
import {Field, reduxForm} from "redux-form";

const NewPost = (props) => {

    let newText = React.createRef()

    const textChange = () => {
        let text = newText.current.value
       props.onTextChange(text);
    }

    const addPost = () => {
       props.onAddPost()
        console.log(props.state)
    }

    return (
        <form className={style.newMessage} onSubmit={props.handleSubmit}>
            <div>
                <Field onChange={textChange} ref={newText} component={"input"}
                       name={"messageText"} placeholder="Write you post text" value={props.state.newText}/>
            </div>
            <div>
                <button onClick={addPost}>Post</button>
            </div>
        </form>
    )
}

const NewPostReduxForm = reduxForm({form:"form"})(NewPost)

const Post = (props) => {
    return (
        <NewPostReduxForm/>
    )
}

export default NewPost
