import React from "react"
import style from "./Post.module.css"
import avatar from "../../../../assets/images/avatar1.jpg"
import like from "../../../../assets/like.png"
import notLike from "../../../../assets/like (2).png"

const Post = (props) => {
    const likeSwitching = () => {
        props.setLikeValue(!props.likeValue)
    }

    return (
        <div className={style.post}>
            <img src={props.avatar ? props.avatar : avatar}/>
            <div className={style.fullName}>{props.fullName}</div>
            <span className={style.postText}>{props.text}</span>
            <div className={style.likeCount}><img className={style.like} src={props.likeValue ? like : notLike}
                                                  onClick={likeSwitching}/>{props.likeCount}</div>
        </div>
    )
}

export default Post
