import style from "./UserInfo.module.css"
import StatusWithHooks from "../ProfileStatus/StatusWithHooks";
import React, {useState} from "react";

const UserInfo = (props) => {

    const contacts = [];

        let contact = props.profile.contacts

        for (let key in contact){
            contacts.push(`${key}: ${contact[key]?contact[key]:"~"}`)
        }
        let contactsElement = contacts.map(c => <div>{c}</div>)

    let [expand, setExpand] = useState(false)

     const expandUnexpand = () => {

     }

    return (
        <div className={style.userInfo}>
            <div className={style.mainInfo}>
                <div className={style.fullName}>{props.profile.fullName}</div>
                <StatusWithHooks status={props.status}
                                 getStatus={props.getStatus} updateStatus={props.updateStatus}/>
            </div>                             
            <div className={style.item}> About me: </div>
            <div> {props.profile.aboutMe ? props.profile.aboutMe : "It's empty here so far"} </div>
            {props.profile.lookingForAJob ?
                <div>props.profile.lookingForAJobDescription</div> :
                "I`m not looking for job"}
            <div onClick={()=>{setExpand}}>Contacts:</div>
           {expand && <div>{contactsElement}</div>}
           {!expand && <div>null</div>}

        </div>
    )
}

export default UserInfo
