import React from "react";
import Header from "./Header";
import {getAuthUserData, Logout} from "../../Redux/authReducer";
import {connect} from "react-redux";
import {withRouter} from "react-router-dom";
import {getAuthUserInform} from "../../Redux/headerReducer";

class HeaderContainer extends React.Component {

    updateHeader() {
        if (this.props.isAuth) {
            let userId = this.props.authorizedUserId
            this.props.getAuthUserInform(userId)
        }
    }

    componentDidMount() {
        this.updateHeader()
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        debugger
        if (prevProps.authProfileData !== this.props.authProfileData) {
            this.updateHeader()
        }

    }

    render() {
        return <Header {...this.props}/>

    }

}

const mapStateToProps = (state) => ({
    isAuth: state.auth.isAuth,
    login: state.auth.login,
    authorizedUserId: state.auth.id,
    authProfileData: state.header.authUserProfile,

});

let WithRouterHeaderComponent = withRouter(HeaderContainer)

export default connect(mapStateToProps, {getAuthUserData, Logout, getAuthUserInform})(WithRouterHeaderComponent)
