import React from "react";
import style from "./Users.module.css"
import User from "./User/User";
import * as axios from "axios";
import avatar from "../../assets/images/avatar1.jpg";


class Users extends React.Component {

    getUsers = () => {
        if (this.props.users.length === 0) {
            axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response => {
                this.props.setUsers(response.data.items)
                console.log(this.props)

            });
        }
    }

    unfollow = () => {
        this.props.unfollow(this.props.id)
    }
    follow = () => {
        this.props.follow(this.props.id)
        console.log(this.props.followed)

    }


    render() {
        return (
            <div className={style.users}>
                <button onClick={this.getUsers}>Get users</button>

                <div className={style.user}>
                    <img src={this.props.avatar !== null ? this.props.avatar : avatar}/>
                    <div> {this.props.name}</div>
                    <div> {this.props.country}</div>
                    <div> {this.props.city}</div>
                    <div>{this.props.followed ? <button onClick={this.unfollow}> Unfollow</button> :
                        <button onClick={this.follow}> Follow</button>}</div>
                </div>

            </div>
        )
    }
}

export default Users
