import React from "react";
import style from "./Login.module.css"
import errorStyle from "../common/FormsControl/FormControl.module.css"
import {Field, reduxForm} from "redux-form";
import {createForm, Input} from "../common/FormsControl/FormsControl";
import {maxLengthCreator, required} from "../../utils/validators/validators";
import {connect} from "react-redux";
import {LogIn} from "../../Redux/authReducer";
import {Redirect} from "react-router-dom";

const maxLength20 = maxLengthCreator(20)

const LoginForm = (props) => {
    return (
        <div>
            <form onSubmit={props.handleSubmit}>

                {createForm(Input, [required, maxLength20], "email", "email", "email") }
                {/*<div>*/}
                {/*    <Field component={Input} validate={[required, maxLength20]} name={"email"} placeholder={"Login"}/>*/}
                {/*</div>*/}
                <div>
                    <Field component={Input} validate={[required, maxLength20]} type={"password"} name={"password"}
                           placeholder={"Password"}/>
                </div>
                <div>
                    <Field component={"input"} name={"rememberMe"} type={"checkbox"}/> remember me
                </div>
                {props.error && <div className={errorStyle.errorField}>
                    {props.error}
                </div>}
                <div>
                    <button>
                        Submit
                    </button>
                </div>
            </form>
        </div>
    )
}

const LoginReduxForm = reduxForm({
    form: "login"
})(LoginForm)


const Login = (props) => {

    const onSubmit = (formData) => {
        props.LogIn(formData.email, formData.password, formData.rememberMe)
        console.log(formData)
    }

    if (props.isAuth) {
        return <Redirect to="/profile"/>
    }

    return (
        <div className={style.login}>
            <h1>Login </h1>
            <LoginReduxForm onSubmit={onSubmit}/>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        isAuth: state.auth.isAuth
    }
}

export default connect(mapStateToProps, {LogIn})(Login)
