import React from "react";
import Profile from "./Profile";
import {getStatus, setUsersProfile, updateStatus} from "../../Redux/profileReducer";
import {connect} from "react-redux";
import {withRouter} from "react-router-dom";
import withAuthRedirect from "../../HOC/wihtAuthRedirect";

class ProfileContainer extends React.Component {

    componentDidMount() {
        let userId = this.props.match.params.userId
        this.props.setUsersProfile(userId)
        this.props.getStatus(userId)
    }

    render() {
        return (
            <Profile {...this.props} profile={this.props.profile}
                     status={this.props.status} updateStatus={this.props.updateStatus}
            />
        )
    }
}

let AuthRedirectComponent = withAuthRedirect(ProfileContainer)


let mapStateToProps = (state) => ({
    profile: state.profilePage.profile,
    status: state.profilePage.status,
})

let WithRouterProfileContainer = withRouter(AuthRedirectComponent)

export default connect(mapStateToProps, {setUsersProfile, getStatus, updateStatus})(WithRouterProfileContainer)
