import React from "react";
import style from "./User.module.css"
import avatar from "../../../assets/images/avatar1.jpg"
import {NavLink} from "react-router-dom";
import * as axios from "axios";
import {followUser, unfollowUser} from "../../../API/api";

const User = (props) => {
    let unfollow = () => {
        props.toggleFollowingProgress(true,props.id)
        unfollowUser(props.id).then(data => {
            if (data.resultCode == 0) {
                props.unfollow(props.id)
            }
            props.toggleFollowingProgress(false,props.id)
        });
    }
    let follow = () => {
        props.toggleFollowingProgress(true, props.id)
        followUser(props.id).then(data => {
            if (data.resultCode == 0) {
                props.follow(props.id)
            }
            props.toggleFollowingProgress(false, props.id)
        });

    }

    let disableButton =  () => {
        props.followingInProgress.some (id=> id ==props.id)
    }
    return (
        <div>
            <NavLink to={"/profile/" + props.id} className={style.user}>
                <img src={props.avatar !== null ? props.avatar : avatar}/>
            </NavLink>

            <div> {props.name}</div>
            <div> {props.country}</div>
            <div> {props.city}</div>
            <div>{props.followed ?
                <button disabled={disableButton} onClick={unfollow}> Unfollow</button> :
                <button disabled={disableButton} onClick={follow}> Follow</button>}</div>
        </div>)
}

export default User
