import {profileAPI, usersAPI} from "../API/api";

const ADD_POST = "ADD_POST";
const UPDATE_NEW_TEXT = "UPDATE_NEW_TEXT"
const SET_USER_PROFILE = "SET_USER_PROFILE";
const SET_USER_STATUS = "SET_USER_STATUS";

let initialisation = {
    posts: [
        {
            id: 1, text: "My first post!", lickCount: 12,
            avatar: "https://upload.wikimedia.org/wikipedia/en/thumb/3/3b/SpongeBob_SquarePants_character.svg/1200px-SpongeBob_SquarePants_character.svg.png"
        },
        {
            id: 2, text: "My first post!", lickCount: 12,
            avatar: "https://upload.wikimedia.org/wikipedia/en/thumb/3/3b/SpongeBob_SquarePants_character.svg/1200px-SpongeBob_SquarePants_character.svg.png"
        },
        {
            id: 3, text: "My first post!", lickCount: 12,
            avatar: "https://upload.wikimedia.org/wikipedia/en/thumb/3/3b/SpongeBob_SquarePants_character.svg/1200px-SpongeBob_SquarePants_character.svg.png"
        },
    ],
    profile: null,
    status: "",
}

const profileReducer = (state = initialisation, action) => {
    switch (action.type) {
        case ADD_POST:
            let newPost = {
                id: 4,
                text: action.newText,
                lickCount: 0,
                avatar: "https://upload.wikimedia.org/wikipedia/en/thumb/3/3b/SpongeBob_SquarePants_character.svg/1200px-SpongeBob_SquarePants_character.svg.png"
            };
            return {
                ...state,
                posts: [...state.posts, newPost],
                newText: ""
            }
        case SET_USER_PROFILE: {
            return {
                ...state, profile: action.profile
            }
        }
        case SET_USER_STATUS: {
            return {...state, status: action.status}
        }

        default:
            return state
    }
}

export const addPostAC = (newText) => {
    return ({type: ADD_POST, newText})
};

export const setProfile = (profile) => ({type: SET_USER_PROFILE, profile});

export const setStatus = (status) => ({type: SET_USER_STATUS, status});

export const setUsersProfile = (userId) => {
    return (dispatch) => {
              usersAPI.setUsers(userId).then(response => {
            dispatch(setProfile(response.data))
        });
    }
}

export const getStatus = (userId) => {
    return (dispatch) => {
        profileAPI.getStatus(userId).then(response => {
            dispatch(setStatus(response.data))
        });
    }
}

export const updateStatus = (status) => {
    return (dispatch) => {
        profileAPI.updateStatus(status).then(response => {
            if (response.data.resultCode === 0) {
                dispatch(setStatus(status))
            }
        });
    }
}

export default profileReducer
