import React from "react"
import style from "./ProfileInfo.module.css"
import Preloader from "../../common/Preloader/Preloader";
import Status from "./ProfileStatus/Status";
import avatar from "../../../assets/images/avatar1.jpg"
import StatusWithHooks from "./ProfileStatus/StatusWithHooks";
import loadFile from "../../../assets/loadFile.png"

const ProfileInfo = (props) => {
    if (!props.profile) {
        return <Preloader/>
    }

    const onMainPhotoSelected = (e) => {
        if (e.target.files.length){
            props.savePhoto(e.target.files[0])
        }
    }

    let cursorIn = false

    let onMouseE = () => {
        cursorIn = true
        // alert(cursorIn)
    }
    let onMouseL = () => {
       cursorIn = false
        // alert(cursorIn)
    }

    return (
        <div className={style.info}>
            <div className={style.avatar}>

                <img onMouseEnter={onMouseE} onMouseLeave={onMouseL} src={props.profile.photos.large !== null ?
                    props.profile.photos.large : avatar}/>
                {(props.isOwner && cursorIn==true)? <div><input className={style.avatarLoader} type={"file"} id={"avatarLoader"} onChange={onMainPhotoSelected}/>
                <label for={"avatarLoader"}> <img src={loadFile}/>Chang photo</label>
                </div>: null}
            </div>
        </div>
    )
}

export default ProfileInfo
