import React from "react"
import NewMessage from "./NewMessage/NewMessage";
import User from "./Dialog/User/User";
import style from "./Dialogs.module.css"
import Message from "./Dialog/Message/Message";
import {reversedCopy} from "../../utils/arrayHalper";

const Dialogs = (props) => {
    let usersElement = null;

    if (props.dialogs) {
        usersElement = props.dialogs.map(u => <User id={u.id}
                                                    avatar={u.photos.small}
                                                    name={u.userName}
                                                    key={u.id}
                                                    dialogs={props.dialogs}
                                                    newMessagesCount={props.newMessagesCount}
                                                    lastUserActivityDate={props.lastUserActivityDate}
                                                    lastDialogActivityDate={props.lastDialogActivityDate}
                                                    hasNewMessages={props.hasNewMessages}
                                                    getMessages={props.getMessages}
                                                    pageCount={props.pageCount}
                                                    messagePageSize={props.messagePageSize}/>)
    }


    let reversMessageElement = null;


    if (props.activeDialogId) {
        let messageElement = props.dialogsPage.messagesItems;
        debugger
        reversMessageElement = reversedCopy(messageElement).map(m => <Message id={m.id} key={m.id} messageText={m.body}
                                                                              recipientId={m.recipientId}
                                                                              senderId={m.senderId}
                                                                              senderName={m.senderName}
                                                                              translatedBody={m.translatedBody}
                                                                              viewed={m.viewed}
                                                                              totalCount={m.totalCount}
                                                                              addedAt={m.addedAt}/>)
    }

    return (

        <div className={style.dialogs}>

            <div className={style.users}>
                {usersElement}

            </div>
            <div className={style.messages}>
                {reversMessageElement}
            </div>

            <div className={style.newMessage}>
                {props.activeDialogId ? <NewMessage dialogsPage={props.dialogsPage}
                                                    sendNewMessage={props.sendNewMessage}
                                                    activeDialogId={props.activeDialogId}
                                                    sendNewMessage={props.sendNewMessage}/> : null}
            </div>
        </div>
    )
}

export default Dialogs
