import React from "react";
import style from "./Pagination.module.css";

const Pagination = ({totalCount, pageSize, currentPage, paginationSize, ...props}) => {
    let pagesCount = Math.ceil(totalCount / pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }


    let border = (value) => {
        if (leftBorder <= 1){
            return (
                value => currentPage && value <= paginationSize
            )}
    }
    let leftBorder = currentPage - Math.ceil((paginationSize - 1)/2);

    let rightBorder = currentPage + Math.ceil((paginationSize - 1)/2);

    let pagination = pages.filter( border)

    console.log( pagination)

    return (
        <div>
            <button type={"button"} className={style.button}> {"<"} </button>
            {pages.map(p => {
                return <span className={currentPage === p && style.selected}
                             onClick={(e) => {
                                 props.onPageChanged(p)
                             }}>{p} </span>
            })}
            <button type={"button"} className={style.button}>{">"}</button>
        </div>
    )
}

export default Pagination
