import React from "react";
import style from "./User.module.css"
import avatar from "../../../assets/images/avatar1.jpg"
import {NavLink} from "react-router-dom";

const User = (props) => {
    let unfollow = () => {
        props.unfollow(props.id)
    }
    let follow = () => {
        props.follow(props.id)
    }
    return (
        <div className={style.user}>
            <div>
                <NavLink to={"/profile/" + props.id} className={style.userPhoto}>
                    <img src={props.avatar !== null ? props.avatar : avatar}/>
                </NavLink>
                <div> {props.name}</div>

            </div>
            <div className={style.statusString}>
                <span>{props.status ? `Status: + ${props.status}` : "I dosen`t have a status"} </span>
            </div>

            {props.isAuth ? <div>{props.followed ?
                <button className={style.UnfollowButton}
                        disabled={props.followingInProgress.some(id => id == props.id)}
                        onClick={unfollow}> Unfollow</button> :
                <button className={style.followButton}
                        disabled={props.followingInProgress.some(id => id == props.id)}
                        onClick={follow}> Follow</button>}
            </div> : null}
        </div>)
}

export default User
