import React from "react"
import styles from "./Status.module.css"

const StatusWithHooks = (props) => {

    return (
        <div className={styles.status}>
            {!this.state.editMode &&
            <div>
                <span> {props.status || "No status"}</span>
            </div>
            }
            {this.state.editMode &&
            <div>
                <input autoFocus={true} />
            </div>
            }
        </div>
    )
}

export default StatusWithHooks
