import * as axios from "axios"


export const inst = () => {
    return axios.create({
        withCredentials: true,
        baseURL: "https://social-network.samuraijs.com/api/1.0/",
        headers: {"API-KEY": "dd8535b9-d495-4b66-9914-969b34776437"},
    });
}
