import React, {useState} from "react";
import style from "./Users.module.css"
import User from "./User/User";
import Pagination from "../common/Pagination/Pagenation";
import {NavLink} from "react-router-dom";

const Users = ({toggleFollowingProgress, followingInProgress, unfollow, follow, ...props}) => {

    const [visibleUsers, setVisibleUsers] = useState(1)

    let followedUsers = () => {
        debugger
        props.users.filter(u => u.followed = true)
    }

        // .map(u => <Users id={u.id} key={u.id}
        //                  avatar={u.photos.small} name={u.name}
        //                  status={u.status}
        //                  followed={u.followed}
        //                  follow={follow}
        //                  unfollow={unfollow}
        //                  followingInProgress={followingInProgress}
        //                  toggleFollowingProgress={toggleFollowingProgress}
        //                  isAuth={props.isAuth}
        //                  authUserId={props.authUserId}/>)

    return (

        <div className={style.users}>
            <div className={style.userMenu}>
                <div>
                    <button onClick={() => {
                        setVisibleUsers(1)
                    }}> All users
                    </button>
                    <button onclick={() => {
                        setVisibleUsers(0)
                        followedUsers()
                        console.log("!")
                    }}> Followed users
                    </button>
                </div>
            </div>
            <div className={style.usersList}>
                <Pagination className={style.pagination} totalCount={props.totalCount}
                            pageSize={props.pageSize}
                            currentPage={props.currentPage}
                            onPageChanged={props.onPageChanged}
                            paginationSize={props.paginationSize}/>
                {visibleUsers ? <div>{props.users.map(u => {
                        return (<User id={u.id} key={u.id} avatar={u.photos.small} name={u.name}
                                      status={u.status}
                                      followed={u.followed}
                                      follow={follow}
                                      unfollow={unfollow}
                                      followingInProgress={followingInProgress}
                                      toggleFollowingProgress={toggleFollowingProgress}
                                      isAuth={props.isAuth}
                                      authUserId={props.authUserId}/>)
                    })}</div> :
                    <div>{followedUsers}</div>}
            </div>
        </div>
    )
}


export default Users
