import React from "react"
import style from "./MyPosts.module.css"
import Post from "./Post/Post";
import NewPost from "./NewPost/NewPost";
import avatar from "../../../assets/images/avatar1.jpg"
const MyPosts = (props) => {
    debugger
    let photo = () => {
        if (props.profilePage.profile.photos ==null) {
            return avatar
        }
        return props.profilePage.profile.photos.small
    }
    let postsElement =
        props.profilePage.posts.map(p => <Post
            lickCount={p.lickCount} text={p.text} key={p.id} avatar={photo}/>)

    return (
        <div className={style.posts}>
            <div>
                <div className={style.info}>
                    <h2>Posts</h2>
                    <NewPost onAddPost={props.onAddPost} onTextChange={props.onTextChange} state={props.profilePage}/>
                </div>
                <div className={style.posts}>
                    {postsElement}
                </div>
            </div>
        </div>
    )
}

export default MyPosts
