import React from "react"
import style from "./MyPosts.module.css"
import Post from "./Post/Post";
import NewPost from "./NewPost/NewPost";
import Preloader from "../../common/Preloader/Preloader";

class MyPosts extends React.Component {

    shouldComponentUpdate(nextProps, nextState) {
        return nextProps != this.props || nextState != this.state 
    }

    render() {
        if (this.props.profilePage.profile == null) {
            return <Preloader/>
        }

        let postsElement =
            this.props.profilePage.posts.map(p => <Post
                lickCount={p.lickCount} text={p.text} key={p.id} avatar={this.props.profilePage.profile.photos.small}/>)

        return (
            <div className={style.posts}>
                <div>
                    <div className={style.info}>
                        <h2>Posts</h2>
                        <NewPost onAddPost={this.props.onAddPost} state={this.props.profilePage}/>
                    </div>
                    <div className={style.posts}>
                        {postsElement}
                    </div>
                </div>
            </div>
        )
    }
}

export default MyPosts
