import React from "react"
import style from "./FormControl.module.css"


export const Textarea = ({input, meta, ...props}) => {

    const error = meta.error && meta.touched && !meta.active;

    const textError = (meta, input) => {
        if (error) return <span className={style.errorText}>{meta.error}</span>
        return undefined
    }

    const textareaError = (meta) => {
        if (error) return style.formControl + " " + style.error
        return undefined
    }

    return (
        <div className={textareaError(meta)}>
            <textarea {...input} {...props}/>

            {textError(meta)}
        </div>
    )
}

export const Input = ({input, meta, ...props}) => {

    const error = meta.error && meta.touched && !meta.active;

    const textError = (meta, input) => {
        if (error) return <span className={style.errorText}>{meta.error}</span>
        return undefined
    }

    const textareaError = (meta) => {
        if (error) return style.formControl + " " + style.error
        return undefined
    }

    return (
        <div className={textareaError(meta)}>
            <textarea {...input} {...props}/>

            {textError(meta)}
        </div>
    )
}
